import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#9d9d9d',
  },
  overlayText: {
    color: '#FFF',
    marginVertical: 10
  },
  overlay: {
    justifyContent: 'center',
    alignItems: 'center',
    width: '100%',
    height: '100%',
  },
  button: {
    backgroundColor: '#FFF',
    marginVertical: 40,
  },
  buttonTitle: {
    color: '#BB1F13',
    fontSize: 25,
    marginHorizontal: 5,
  },
});

export default styles;
